import React from'react'

class Checkboxes extends React.Component{
    allcheckbox(event) {
        var all = document.getElementsByClassName("item")


        if ( event.target.checked === true) {
            for (var cn in all) {
                if (all[cn].checked === false) {
                    all[cn].checked = true;
                }
            }
        }

        if ( event.target.checked === false) {
            for (var cn1 in all) {
                if (all[cn1].checked === true) {
                    all[cn1].checked = false;
                }
            }
        }
    }
    render () {
        return (
            <div className="container">
                <input type="checkbox" onChange={this.allcheckbox}></input>Select All<br></br>
                <input type="checkbox" class= "item" id="item-1"></input>Item 1<br></br>
                <input type="checkbox" class= "item" id ="item-2"></input>Item 2<br></br>
                <input type="checkbox" class= "item" id="item-3"></input>Item 3<br></br>
                <input type="checkbox" class= "item" id="item-4"></input>Item 4<br></br>
            </div>
        )
    }
}
export default Checkboxes
