import React from'react'

class Checkbox extends React.Component{
    allcheckbox(event) {
        var all = document.getElementsByTagName('input')
        if ( event.target.checked === true) {
            for(var cn in all) {
                if (all[cn].checked === false) {
                    all[cn].checked = true;
                }
            }
        }
    }
    render () {
        return (
            <div className="container">
                <input type="checkbox" onChange={this.allcheckbox}></input>Select All<br></br>
                <input type="checkbox" name= "check-1"></input>Item 1<br></br>
                <input type="checkbox" name= "check-2"></input>Item 2<br></br>
                <input type="checkbox" name= "check-3"></input>Item 3<br></br>
                <input type="checkbox" name= "check-4"></input>Item 4<br></br>
            </div>
        )
    }
}
export default Checkbox
