import React from 'react';
import './App.css';

class Checkbox extends React.Component {
  AllCheck(event) {
    var all = document.getElementsByTagName("input");

    if (event.target.checked) {
      for (var cn in all) {
        if (all[cn].checked === 'false')
          all[cn] = 'true';
      }
    }
  }
}

function App() {



  return (
    <div className="App">
      <header className="App-header">
      </header>
      <body>
        <form>
          <label>
            <input type="checkbox" class = "Checkbox" name="check-all" id="check-all" onChange={this.AllCheck}/>
            Select all
          </label>
          <br></br>
          <label>
            <input type="checkbox" name="check-1" id="check-1"/>
            Item 1
          </label>
          <br></br>
          <label>
            <input type="checkbox" name="check-2" id="check-2"/>
            Item 2
          </label>
          <br></br>
          <label>
            <input type="checkbox" name="check-3" id="check-3"/>
            Item 3
          </label>
          <br></br>
          <label>
            <input type="checkbox" name="check-4" id="check-4"/>
            Item 4
          </label>
        </form>
      </body>
    </div>
  );
}

export default App;
